mapper.insert:

INSERT INTO board 
	( seqno
	  , title
	  , subtitle
	  , content
	  , writer )
VALUES ( #{seqno}
	  , #{title}
	  , #{subtitle}
	  , #{content}
	  , #{writer} )

mapper.update:

UPDATE board
SET seqno=#{seqno}
	, title=#{title}
	, subtitle=#{subtitle}
	, content=#{content}
	, writer=#{writer}

mapper.select:
SELECT seqno , title , subtitle , content , writer FROM board WHERE 1=1
AND seqno = #{searchKeyword}
AND title LIKE CONCAT('%',#{searchKeyword},'%')
ORDER BY limit #{recordCountPerPage} offset #{firstIndex}

SELECT COUNT(*) totcnt FROM board WHERE 1=1
AND seqno = #{searchKeyword}
AND title LIKE CONCAT('%',#{searchKeyword},'%')
